package io.codelex.flightplanner.maintree.repositories;import io.codelex.flightplanner.domain.Airport;import io.codelex.flightplanner.domain.Flight;import io.codelex.flightplanner.dto.AddFlightRequest;import io.codelex.flightplanner.dto.FlightSearch;import io.codelex.flightplanner.dto.PageResults;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface FlightRepository {    Flight getFlight(int input);    Flight addFlight(AddFlightRequest input);    void removeFlight(int id);    List<Airport> searchAirports(String input);    PageResults<Flight> searchFlights(FlightSearch input);    void clearFlights();}